import React, { useState } from 'react';
import TaskList from './TaskList';
import Navbar from './Navbar';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure Bootstrap is imported

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const addTask = () => {
    if (newTask) {
      setTasks([...tasks, { id: Date.now(), text: newTask, completed: false }]);
      setNewTask('');
    }
  };

  const completeTask = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const removeTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
  };

  return (
    <>
      <Navbar />
      <div className="App container">
        <h1 className='text'>Todo List</h1>
        <div className="card" style={{ width: '18rem' }}>
          <img src="https://th.bing.com/th/id/OIG4.RdxDOgVpwkACcHXpx8lo?w=173&h=173&c=6&r=0&o=5&dpr=1.3&pid=ImgGn" className="card-img-top" alt="..." />
          <div className="card-body">
            <h5 className="card-title">ToDo List </h5>

          </div>
        </div>
        <input
          className='form-control mt-3'
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task"
          style={{ backgroundColor: 'rgb(105, 236, 105)', placeholderTextColor: 'black' }}
        />
        <button className='btn btn-outline-success mt-2' onClick={addTask}>Add</button>
        <TaskList tasks={tasks} onTaskComplete={completeTask} onTaskRemove={removeTask} />
      </div>
    </>
  );
}

export default App;
