import React, { useState } from 'react';
import TaskList from './TaskList';
import Navbar from './Navbar';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const addTask = () => {
    if (newTask) {
      setTasks([...tasks, { id: Date.now(), text: newTask, completed: false }]);
      setNewTask('');
    }
  };

  const completeTask = (taskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const removeTask = (taskId) => {
    setTasks(tasks.filter((task) => task.id !== taskId));
  };

  return (
    <>
      <Navbar />
      <div className="App container">
        <h1 className='text'>Todo List</h1>
        <input
        className='input'
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="<h1>Add a new task</h1"
        />
        <button className='btn btn-outline-success' onClick={addTask}>Add</button>
        <TaskList tasks={tasks} onTaskComplete={completeTask} onTaskRemove={removeTask} />
      </div>
    </>
  );
}

export default App;
